import React, { Component } from 'react';
import LoadingGif from './../../public/images/loading.gif'

export default class LoaderComponent extends Component {
   constructor(props) {
      super(props);
      this.state = {
         loading: false
       }
      }

   render()
   {
    let data;
    if (this.state.loading) {
      data = <img  data-src={LoadingGif} />
    } else {
      data = undefined;
    }
      return(
      <div>
            {data}
       </div>
      )
   }
}
